Desktop::Desktop(Draw *draw, uint16_t textColor, uint16_t backgroundColor)
    : display(draw), textColor(textColor), backgroundColor(backgroundColor),
      hasBattery(true), hasBluetooth(false), hasWiFi(false), darkMode(false),
      boardName(nullptr), rtcTime(nullptr)
{
    this->clear();
    this->darkMode = textColor == TFT_WHITE && backgroundColor == TFT_BLACK;
#ifdef CYW43_WL_GPIO_LED_PIN
    this->hasWiFi = true;
    this->hasBluetooth = true;
#endif
    this->boardName = System::getDeviceName();
}

Desktop::~Desktop()
{
}

void Desktop::clear()
{
    display->clear(Vector(0, 0), display->getSize(), backgroundColor);
    display->swap();
}

void Desktop::draw(const uint8_t *animationFrame, Vector animationSize, const uint16_t *palette)
{
    display->clear(Vector(0, 0), display->getSize(), backgroundColor);
    drawHeader();
    display->image(Vector(0, 20), animationFrame, Vector(animationSize.x, animationSize.y - 20), palette);
    display->swap();
}

void Desktop::drawHeader()
{
    // draw board name
    display->text(Vector(2, 5), boardName, textColor);

    // draw time if set
    if (rtcTime != nullptr && strlen(rtcTime) > 0)
    {
        display->text(Vector(140, 5), rtcTime, textColor);
    }

    // draw WiFi icon
    display->image(Vector(display->getSize().x - 21, 2), hasWiFi ? WIFI_ON_BLACK : WIFI_OFF_BLACK, Vector(19, 16), nullptr, false, darkMode);

    // draw Bluetooth icon
    display->image(Vector(display->getSize().x - 40, 2), hasBluetooth ? BLUETOOTH_ON_BLACK : BLUETOOTH_OFF_BLACK, Vector(14, 16), nullptr, false, darkMode);

    // draw battery percentage
    char batteryText[10];
    if (hasBattery)
    {
        int raw_level = sb_read_battery();
        int battery_level = raw_level & 0x7F; // Mask out the charging bit
        // bool charging = (raw_level & 0x80) != 0; // Check if charging
        snprintf(batteryText, sizeof(batteryText), "%d%%", battery_level);
        display->text(Vector(display->getSize().x - 68, 5), batteryText, textColor);
    }
    else
    {
        display->text(Vector(display->getSize().x - 68, 5), "100%", textColor);
    }
}
